    -- DO NOT EDIT BELOW THIS LINE

    table.lower(Config.SafeList)

    function table.findtext(self, v)
        local c = table.find(self, v)
        if not c then
            for i, k in pairs(self) do
                if v:find(k) or v:lower():find(k:lower()) then
                    return i
                end
            end
        else
            return c
        end
        return nil
    end

	if not file.exists(string.format("[%s] - Auto Responder.txt", $name)) then
		file.clear(string.format("[%s] - Auto Responder.txt", $name))
	end

init end

auto(800, 1000)

foreach newmessage m do
    local Type, Time, Sender, Level, Message = m.type, os.date('%H:%M'), m.sender, m.level, m.content
    if Sender:lower() ~= $name:lower() and table.find({1, 6}, Type) and (not table.find(Config.SafeList, Sender:lower())) and Level >= Config.MinLevel and os.difftime(os.time(), Responder.Timer) >= 2 and (not Responder.Ignored["" .. Sender .. ""]) and #spellinfo(Message).words == 0 then
		file.writeline(string.format("[%s] - Auto Responder.txt", $name), string.format("RECEIVED: [%s] VIA: %s FROM: %s CONTENT: %s", Time, Type == 6 and "Private" or "Local Chat", Sender, Message))
        for State, Entry in pairs(Responder.SpeechWords) do
            if table.findtext(Entry.k, Message) then
                if Responder.MessageInfo["" .. Sender .. ""] then
                    if Responder.MessageInfo["" .. Sender .. ""][State] then
                        if not Responder.SpeechWords[State].r[Responder.MessageInfo["" .. Sender .. ""][State] + 1] then
                            Responder.Ignored["" .. Sender .. ""] = true
							local msg = ({"ignored -.-", "ignored"})[math.random(1, 2)]
			                if not Config.IgnoreMonsters then
			                    while maround(1, false) > 0 do
                        			wait(100, 200)
            			        end
			                end
                            if Type == 1 then
                                say("Local Chat", msg)
                            elseif Type == 6 then
                                say("Local Chat", string.format("@%s@ %s", Sender, msg))
                            end
							file.writeline(string.format("[%s] - Auto Responder.txt", $name), string.format("SENT: [%s] VIA: %s FROM: %s TO: %s CONTENT: %s", Time, Type == 6 and "Private" or "Local Chat", $name, Sender, msg))
							file.writeline(string.format("[%s] - Auto Responder.txt", $name), string.format("IGNORED PLAYER: [%s] NAME: %s", Time, Sender))
                            return
                        end
                    else
                        Responder.MessageInfo["" .. Sender .. ""][State] = 0
                    end
                else
                    Responder.MessageInfo["" .. Sender .. ""] = {}
                    Responder.MessageInfo["" .. Sender .. ""][State] = 0
                end
                if not Config.IgnoreMonsters then
                    while maround(1, false) > 0 do
                        wait(100, 200)
                    end
                end
                local msg = Entry.r[Responder.MessageInfo["" .. Sender .. ""][State] + 1][math.random(1, #Entry.r[Responder.MessageInfo["" .. Sender .. ""][State] + 1])]
                if Type == 1 then
                    say("Local Chat", msg)
                elseif Type == 6 then
                    say("Local Chat", string.format("*%s* %s", Sender, msg))
                end
				file.writeline(string.format("[%s] - Auto Responder.txt", $name), string.format("SENT: [%s] VIA: %s FROM: %s TO: %s CONTENT: %s", Time, Type == 6 and "Private" or "Local Chat", $name, Sender, msg))
                Responder.MessageInfo["" .. Sender .. ""][State], Responder.Timer = Responder.MessageInfo["" .. Sender .. ""][State] + 1, os.time()
                return
            end
        end
    end
end
