Waypoint.script:
wait(700)
if $posz ~= $wptz then gotolabel($wptid-2) 
else gotolabel(0, 'hunt')
end

local icegolem = getuseroption('Ice Golem')

wait(300)
if icegolem == false then gotolabel(0, 'crystal spiders') 
else gotolabel('start hunt')
end

local crystalspider = getuseroption('Crystal Spider')

wait(300)
if crystalspider == true then gotolabel(0, 'crystal spiders') 
else gotolabel('start hunt')
end

wait(700)
if $posz ~= $wptz then gotolabel($wptid-3)
else gotolabel(0, 'to spawn') end
